import React, { useEffect } from "react";
import ReactApexChart from "react-apexcharts";

interface ChartData {
  data: number[];
}

const ChartDemo: React.FC = () => {
  useEffect(() => {
    const chartOptions: ApexCharts.ApexOptions = {
      chart: {
        type: "donut",
        fontFamily: "inherit",
        height: 240,
        sparkline: {
          enabled: true,
        },
        animations: {
          enabled: false,
        },
      },
      fill: {
        opacity: 1,
      },
      series: [
        {
          data: [44, 55, 12, 2],
        },
      ],
      labels: ["Direct", "Affilliate", "E-mail", "Other"],
      tooltip: {
        theme: "dark",
        fillSeriesColor: false,
      },
      grid: {
        strokeDashArray: 4,
      },
      colors: ["#007bff", "#0056b3", "#003980", "#ced4da"],
      legend: {
        show: true,
        position: "bottom",
        offsetY: 12,
        markers: {
          width: 10,
          height: 10,
          radius: 100,
        },
        itemMargin: {
          horizontal: 8,
          vertical: 8,
        },
      },
    };

    // Render the chart
    const renderChart = () => {
      return (
        <ReactApexChart
          options={chartOptions}
          series={chartOptions.series}
          type="donut"
          height={240}
        />
      );
    };

    if (document.getElementById("chart-demo-pie")) {
      renderChart();
    }
  }, []);

  return (
    <div className="card">
      <div className="card-body">
        <div id="chart-demo-pie" className="chart-lg"></div>
      </div>
    </div>
  );
};

export default ChartDemo;
