import "./EMICalculation.css";
const EMICalculation: React.FC<{
  monthly_emi: number;
  clicked: boolean;
  total_pay: number;
  total_interest: number;
}> = (props) => {
  return (
    <div>
      {props.clicked && (
        <div className="calculated-emi">
          Your Monthly EMI will be
          <span
            style={{ fontWeight: "700", fontSize: "28px" }}
          >{` ₹${props.monthly_emi.toFixed(0)} `}</span>
          per month
          <span style={{ fontWeight: "700", fontSize: "28px" }}>
            Total Payment will be {` ₹${props.total_pay.toFixed(0)} `}
          </span>
          <span style={{ fontWeight: "700", fontSize: "28px" }}>
            Total Interest will be {` ₹${props.total_interest.toFixed(0)} `}
          </span>
        </div>
      )}
    </div>
  );
};
export default EMICalculation;
