import { useState } from "react";
const EMICalculation: React.FC<{
  principal: number;
  tenure: number;
  interest: number;
}> = (props) => {
  const [emi, setEMI] = useState(0);
  let monthlyInterest = props.interest / 12 / 100;
  let months = props.tenure * 12;
  let numerator: number =
    props.principal * monthlyInterest * (1 + monthlyInterest) ** months;
  let denominator: number = (1 + monthlyInterest) ** months - 1;
  setEMI(numerator / denominator);
  return <div>{`MOnthly EMI is${emi}`}</div>;
};
export default EMICalculation;
