import React from "react";
import { useState } from "react";
const Calculation: React.FC = () => {
  const [principalSliderValue, setSliderPrincipalValue] = useState(0);
  const [principalInputValue, setPrincipalInputValue] = useState("");

  const handleSliderChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setSliderPrincipalValue(parseInt(event.target.value));
    setPrincipalInputValue(String(event.target.value));
  };
  const principalInputChangeHandler = (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setPrincipalInputValue(String(event.target.value));
    setSliderPrincipalValue(parseInt(event.target.value));
  };
  return (
    <div>
      <div>
        <h1>HDFC LOAN CALCULATOR</h1>
        <label>Amount you need</label>
        <input
          type="text"
          defaultValue=""
          value={`₹${principalInputValue}`}
          onChange={principalInputChangeHandler}
        />
        <br />
        <input
          type="range"
          value={principalSliderValue}
          width="214px"
          min={1}
          max={50}
          onChange={handleSliderChange}
        />
      </div>
    </div>
  );
};
export default Calculation;
