import React from "react";
import { useState } from "react";
import EMICalculation from "./EMICalculation";
import "./EMIInputForm.css";
const EMIInputForm: React.FC = () => {
  const [principalSliderValue, setSliderPrincipalValue] = useState(0);
  const [principalInputValue, setPrincipalInputValue] = useState(50000);
  const [tenureSliderValue, setSliderTenureValue] = useState(5);
  const [tenureInputValue, setTenureInputValue] = useState(5);
  const [interestSliderValue, setSliderInterestValue] = useState(0);
  const [interestInputValue, setInterestInputValue] = useState(10.5);
  const [emi, setEMI] = useState(0);
  const [totalPay, setTotalPay] = useState(0);
  const [totalInterest, setTotalInterest] = useState(0);
  const [clicked, setClicked] = useState(false);

  const principalSliderChangeHandler = (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setSliderPrincipalValue(parseInt(event.target.value));
    setPrincipalInputValue(parseInt(event.target.value));
  };
  const principalInputChangeHandler = (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setPrincipalInputValue(parseFloat(event.target.value));
    setSliderPrincipalValue(parseInt(event.target.value));
  };

  const tenureSliderChangeHandler = (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setSliderTenureValue(parseInt(event.target.value));
    setTenureInputValue(parseInt(event.target.value));
  };

  const tenureInputChangeHandler = (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setTenureInputValue(parseInt(event.target.value));
    setSliderTenureValue(parseInt(event.target.value));
  };

  const interestSliderChangeHandler = (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setSliderInterestValue(parseInt(event.target.value));
    setInterestInputValue(parseInt(event.target.value));
  };

  const interestInputChangeHandler = (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setInterestInputValue(parseInt(event.target.value));
    setSliderInterestValue(parseInt(event.target.value));
  };
  const principalSlider = document.getElementById(
    "principalSlider"
  ) as HTMLInputElement;

  principalSlider?.addEventListener("input", function () {
    const percent =
      ((parseInt(principalSlider.getAttribute("value") || "0") -
        parseInt(principalSlider.min)) /
        (parseInt(principalSlider.max) - parseInt(principalSlider.min))) *
      100;
    const bg = `linear-gradient(to right, #1e436f 0%, #1e436f ${percent}%, #ffff ${percent}%, #ffff 100%)`;
    principalSlider.style.background = bg;
  });
  const tenureSlider = document.getElementById(
    "tenureSlider"
  ) as HTMLInputElement;

  tenureSlider?.addEventListener("input", function () {
    const percent =
      ((parseInt(tenureSlider.getAttribute("value") || "0") -
        parseInt(tenureSlider.min)) /
        (parseInt(tenureSlider.max) - parseInt(tenureSlider.min))) *
      100;
    const bg = `linear-gradient(to right, #1e436f 0%, #1e436f ${percent}%, #ffff ${percent}%, #ffff 100%)`;
    tenureSlider.style.background = bg;
  });
  const interestSlider = document.getElementById(
    "interestSlider"
  ) as HTMLInputElement;

  interestSlider?.addEventListener("input", function () {
    const percent =
      ((parseInt(interestSlider.getAttribute("value") || "0") -
        parseInt(interestSlider.min)) /
        (parseInt(interestSlider.max) - parseInt(interestSlider.min))) *
      100;
    const bg = `linear-gradient(to right, #1e436f 0%, #1e436f ${percent}%, #ffff ${percent}%, #ffff 100%)`;
    interestSlider.style.background = bg;
  });
  const SubmitHandler = (event: React.FormEvent) => {
    console.log("button clicked");
    event.preventDefault();
    let monthlyInterest = interestInputValue / 12 / 100;
    let months = tenureInputValue * 12;
    let numerator: number =
      principalInputValue * monthlyInterest * (1 + monthlyInterest) ** months;
    let denominator: number = (1 + monthlyInterest) ** months - 1;
    const total_pay = (numerator / denominator) * months;
    const total_interest = total_pay - principalInputValue;
    setEMI(numerator / denominator);
    setClicked(true);
    setTotalPay(total_pay);
    setTotalInterest(total_interest);
  };
  return (
    <form>
      <div className="Card">
        <div className="card-contents">
          <div className="Principal">
            <section
              style={{
                width: "140%",
                display: "flex",
                alignItems: "center",
                justifyContent: "space-between",
              }}
            >
              <label>Amount you need</label>
              <input
                style={{ width: "70px" }}
                type="number"
                value={principalInputValue}
                onChange={principalInputChangeHandler}
              />
            </section>
            <input
              className="personal-loan-slider"
              id="principalSlider"
              type="range"
              value={principalSliderValue}
              min={50000}
              max={4000000}
              onChange={principalSliderChangeHandler}
            />
            <div
              style={{
                width: "150%",
                display: "flex",
                justifyContent: "space-around",
              }}
            >
              <p>₹5,00,000</p>
              <p>₹40,00,000</p>
            </div>
          </div>
          <div className="Tenure">
            <section
              style={{
                width: "120%",
                display: "flex",
                alignItems: "center",
                justifyContent: "space-between",
              }}
            >
              <label>for</label>
              <input
                style={{ width: "30px" }}
                type="number"
                value={tenureInputValue}
                onChange={tenureInputChangeHandler}
              />
              <label>years</label>
            </section>
            <input
              className="tenure-slider"
              id="tenureSlider"
              type="range"
              value={tenureSliderValue}
              width="214px"
              min={1}
              max={5}
              onChange={tenureSliderChangeHandler}
            />
            <div
              style={{
                width: "150%",
                display: "flex",
                justifyContent: "space-around",
              }}
            >
              <p>1 Year</p>
              <p>5 Years</p>
            </div>
          </div>
          <div className="Interest">
            <section
              style={{
                width: "120%",
                display: "flex",
                alignItems: "center",
                justifyContent: "space-between",
              }}
            >
              <label>Interest rate</label>
              <input
                style={{ width: "40px" }}
                type="number"
                value={interestInputValue}
                onChange={interestInputChangeHandler}
              />
              <label>%</label>
            </section>
            <input
              className="interest-slider"
              id="interestSlider"
              type="range"
              value={interestSliderValue}
              width="214px"
              min={10.5}
              max={22}
              onChange={interestSliderChangeHandler}
            />
            <div
              style={{
                width: "130%",
                display: "flex",
                justifyContent: "space-around",
              }}
            >
              <p>10.5%</p>
              <p>21%</p>
            </div>
          </div>
        </div>
        <button onClick={SubmitHandler}>Calculate</button>
        <EMICalculation
          monthly_emi={emi}
          clicked={clicked}
          total_pay={totalPay}
          total_interest={totalInterest}
        />
      </div>
    </form>
  );
};
export default EMIInputForm;
