import React, { useEffect, useState } from "react";
import ReactApexChart from "react-apexcharts";
import "./ChartDemo.css";

const ChartDemo: React.FC<{ amount: number; interest: number }> = (props) => {
  const [chartOptions, setChartOptions] = useState<ApexCharts.ApexOptions>({
    // initial chart options
    chart: {
      type: "pie",
      fontFamily: "inherit",
      height: 240,
      sparkline: {
        enabled: true,
      },
      animations: {
        enabled: true,
      },
    },
    fill: {
      opacity: 1,
    },
    series: [
      {
        data: [props.amount, props.interest],
      },
    ],
    labels: ["Total Amount", "Interest"],
    tooltip: {
      theme: "dark",
      fillSeriesColor: true,
    },
    grid: {
      strokeDashArray: 4,
    },
    colors: ["#007bff", "#0056b3"],
    legend: {
      show: true,
      position: "bottom",
      offsetY: 12,
      markers: {
        width: 10,
        height: 10,
        radius: 100,
      },
      itemMargin: {
        horizontal: 8,
        vertical: 8,
      },
    },
  });

  useEffect(() => {
    setChartOptions((prevOptions) => ({
      ...prevOptions,
      series: [
        {
          data: [props.amount, props.interest],
        },
      ],
    }));
  }, [props.amount]);

  return (
    <div className="card">
      <div className="card-body">
        <div id="chart-demo-pie" className="chart-lg">
          <ReactApexChart
            options={chartOptions}
            series={chartOptions.series}
            type="pie"
            height={240}
          />
        </div>
      </div>
    </div>
  );
};

export default ChartDemo;
