import React from "react";
import { useState } from "react";
import "./Calculation.css";
const Calculation: React.FC = () => {
  const [principalSliderValue, setSliderPrincipalValue] = useState(0);
  const [principalInputValue, setPrincipalInputValue] = useState("50000");
  const [tenureSliderValue, setSliderTenureValue] = useState(0);
  const [tenureInputValue, setTenureInputValue] = useState(5);
  const [interestSliderValue, setSliderInterestValue] = useState(0);
  const [interestInputValue, setInterestInputValue] = useState(10.5);

  const principalSliderChangeHandler = (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setSliderPrincipalValue(parseInt(event.target.value));
    setPrincipalInputValue(String(event.target.value));
  };
  const principalInputChangeHandler = (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setPrincipalInputValue(String(event.target.value));
    if (!principalInputValue) {
      setSliderPrincipalValue(parseInt(event.target.value));
    } else {
      setSliderPrincipalValue(parseInt(principalInputValue));
    }
  };

  const tenureSliderChangeHandler = (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setSliderTenureValue(parseInt(event.target.value));
    setTenureInputValue(parseInt(event.target.value));
  };

  const tenureInputChangeHandler = (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setTenureInputValue(parseInt(event.target.value));
    if (!tenureInputValue) {
      setSliderTenureValue(parseInt(event.target.value));
    } else {
      setSliderTenureValue(tenureInputValue);
    }
  };

  const interestSliderChangeHandler = (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setSliderInterestValue(parseInt(event.target.value));
    setInterestInputValue(parseInt(event.target.value));
  };

  const interestInputChangeHandler = (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setInterestInputValue(parseInt(event.target.value));
    if (!interestInputValue) {
      setSliderInterestValue(parseInt(event.target.value));
    } else {
      setSliderInterestValue(interestInputValue);
    }
  };
  return (
    <form>
      <div className="Card">
        <div className="Principal">
          <section style={{ display: "flex", justifyContent: "space-between" }}>
            <label>Amount you need</label>
            <input
              style={{ width: "79px", padding: "6px 0 6px 15px" }}
              type="text"
              value={`â‚¹${principalInputValue}`}
              onChange={principalInputChangeHandler}
            />
          </section>
          <br />
          <input
            className="personal-loan-slider"
            type="range"
            value={principalSliderValue}
            min={50000}
            max={4000000}
            onChange={principalSliderChangeHandler}
          />
          <div style={{ display: "flex", justifyContent: "space-between" }}>
            <p>â‚¹50000</p>
            <p>â‚¹4000000</p>
          </div>
        </div>
        <div className="Tenure">
          <label>for</label>
          <input
            style={{ width: "45px", padding: "1px 2px" }}
            type="text"
            value={tenureInputValue}
            onChange={tenureInputChangeHandler}
          />
          <label>years</label>
          <br />
          <input
            className="tenure-slider"
            type="range"
            value={tenureSliderValue}
            width="214px"
            min={1}
            max={5}
            onChange={tenureSliderChangeHandler}
          />
          <div style={{ display: "flex", justifyContent: "space-between" }}>
            <p>1 Year</p>
            <p>5 Years</p>
          </div>
        </div>
        <div className="Interest">
          <label>Interest rate</label>
          <input
            style={{ width: "55px", padding: "6px 8px" }}
            type="text"
            value={interestInputValue}
            onChange={interestInputChangeHandler}
          />
          <label>%</label>
          <br />
          <input
            className="interest-slider"
            type="range"
            value={interestSliderValue}
            width="214px"
            min={10.5}
            max={22}
            onChange={interestSliderChangeHandler}
          />
          <div style={{ display: "flex", justifyContent: "space-between" }}>
            <p>10.5%</p>
            <p>21%</p>
          </div>
        </div>
        <button>Calculate</button>
      </div>
    </form>
  );
};
export default Calculation;
