import ChartDemo from "./ChartDemo";
import "./EMICalculation.css";
const EMICalculation: React.FC<{
  monthly_emi: number;
  clicked: boolean;
  total_pay: number;
  total_interest: number;
}> = (props) => {
  return (
    <div>
      {props.clicked && (
        <div className="calculated-emi">
          Your Monthly EMI will be
          <span
            style={{ fontWeight: "700", fontSize: "28px" }}
          >{` ₹${props.monthly_emi.toFixed(0)} `}</span>
          per month
          <ChartDemo amount={total_pay} interest={total_interest} />
        </div>
      )}
    </div>
  );
};
export default EMICalculation;
