import React, { useEffect } from "react";
import ReactApexChart from "react-apexcharts";
import "./ChartDemo.css";

const ChartDemo: React.FC<{ amount: number; interest: number }> = (props) => {
  useEffect(() => {
    const chartOptions: ApexCharts.ApexOptions = {
      chart: {
        type: "donut",
        fontFamily: "inherit",
        height: 240,
        sparkline: {
          enabled: true,
        },
        animations: {
          enabled: false,
        },
      },
      fill: {
        opacity: 1,
      },
      series: [
        {
          data: [props.amount, props.interest],
        },
      ],
      labels: ["Total Amount", "Interest"],
      tooltip: {
        theme: "dark",
        fillSeriesColor: false,
      },
      grid: {
        strokeDashArray: 4,
      },
      colors: ["#007bff", "#0056b3"],
      legend: {
        show: true,
        position: "bottom",
        offsetY: 12,
        markers: {
          width: 10,
          height: 10,
          radius: 100,
        },
        itemMargin: {
          horizontal: 8,
          vertical: 8,
        },
      },
    };

    // Render the chart
    const renderChart = () => {
      if (document.getElementById("chart-demo-pie")) {
        ReactApexChart.render(
          <ReactApexChart
            options={chartOptions}
            series={chartOptions.series}
            type="donut"
            height={240}
          />,
          document.getElementById("chart-demo-pie")
        );
      }
    };

    renderChart();
  }, [props.amount, props.interest]);

  return (
    <div className="card">
      <div className="card-body">
        <div id="chart-demo-pie" className="chart-lg"></div>
      </div>
    </div>
  );
};

export default ChartDemo;
