import React from "react";
import { useState } from "react";
const Calculation: React.FC = () => {
  const [principalSliderValue, setSliderPrincipalValue] = useState(0);
  const [principalInputValue, setPrincipalInputValue] = useState("");
  const [tenureSliderValue, setSliderTenureValue] = useState(0);
  const [tenurelInputValue, setTenureInputValue] = useState(0);
  const [interestSliderValue, setSliderInterestValue] = useState(0);
  const [interestInputValue, setInterestInputValue] = useState(0);

  const principalSliderChangeHandler = (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setSliderPrincipalValue(parseInt(event.target.value));
    setPrincipalInputValue(String(event.target.value));
  };
  const principalInputChangeHandler = (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setPrincipalInputValue(String(event.target.value));
    setSliderPrincipalValue(parseInt(event.target.value));
  };

  const tenureSliderChangeHandler = (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setSliderTenureValue(parseInt(event.target.value));
    setTenureInputValue(parseInt(event.target.value));
  };

  const tenureInputChangeHandler = (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setTenureInputValue(parseInt(event.target.value));
    setSliderPrincipalValue(parseInt(event.target.value));
  };

  const interestSliderChangeHandler = (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setSliderInterestValue(parseInt(event.target.value));
    setInterestInputValue(parseInt(event.target.value));
  };

  const interestInputChangeHandler = (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setInterestInputValue(parseInt(event.target.value));
    setSliderInterestValue(parseInt(event.target.value));
  };
  return (
    <div>
      <div>
        <h1>HDFC LOAN CALCULATOR</h1>
        <label>Amount you need</label>
        <input
          type="text"
          defaultValue=""
          value={`₹${principalInputValue}`}
          onChange={principalInputChangeHandler}
        />
        <br />
        <input
          type="range"
          value={principalSliderValue}
          width="214px"
          min={1}
          max={50}
          onChange={principalSliderChangeHandler}
        />
      </div>
      <div>
        <label>for</label>
        <input
          type="text"
          defaultValue=""
          value={tenurelInputValue}
          onChange={tenureInputChangeHandler}
        />
        <label>years</label>
        <br />
        <input
          type="range"
          value={tenureSliderValue}
          width="214px"
          min={1}
          max={5}
          onChange={tenureSliderChangeHandler}
        />
      </div>
      <div>
        <label>Interest Rate</label>
        <input
          type="text"
          defaultValue={10.5}
          value={interestInputValue}
          onChange={interestInputChangeHandler}
        />
        <label>%</label>
        <br />
        <input
          type="range"
          value={interestSliderValue}
          width="214px"
          min={10.5}
          max={21}
          onChange={interestSliderChangeHandler}
        />
      </div>
    </div>
  );
};
export default Calculation;
