import React, { useState, ChangeEvent, FormEvent } from "react";
import EMICalculation from "./EMICalculation";
import "./EMIInputForm.css";

interface InputProps {
  label: string;
  value: number;
  onChange: (event: ChangeEvent<HTMLInputElement>) => void;
}

const InputField: React.FC<InputProps> = ({ label, value, onChange }) => (
  <section
    style={{
      width: "120%",
      display: "flex",
      alignItems: "center",
      justifyContent: "space-between",
    }}
  >
    <label>{label}</label>
    <input
      style={{ width: "70px" }}
      type="text"
      value={value}
      onChange={onChange}
    />
  </section>
);

const SliderField: React.FC<InputProps & { min: number; max: number }> = ({
  label,
  value,
  min,
  max,
  onChange,
}) => (
  <>
    <InputField label={label} value={value} onChange={onChange} />
    <input
      className="slider"
      type="range"
      value={value}
      min={min}
      max={max}
      onChange={onChange}
    />
    <div
      style={{
        width: "150%",
        display: "flex",
        justifyContent: "space-around",
      }}
    >
      <p>{min}</p>
      <p>{max}</p>
    </div>
  </>
);

const EMIInputForm: React.FC = () => {
  const [principalSliderValue, setPrincipalSliderValue] = useState(0);
  const [principalInputValue, setPrincipalInputValue] = useState(50000);
  const [tenureSliderValue, setTenureSliderValue] = useState(5);
  const [tenureInputValue, setTenureInputValue] = useState(5);
  const [interestSliderValue, setInterestSliderValue] = useState(0);
  const [interestInputValue, setInterestInputValue] = useState(10.5);
  const [emi, setEMI] = useState(0);
  const [clicked, setClicked] = useState(false);

  const handlePrincipalSliderChange = (
    event: ChangeEvent<HTMLInputElement>
  ) => {
    const value = parseInt(event.target.value);
    setPrincipalSliderValue(value);
    setPrincipalInputValue(value);
  };

  const handlePrincipalInputChange = (event: ChangeEvent<HTMLInputElement>) => {
    const value = parseInt(event.target.value);
    setPrincipalInputValue(value);
    if (!principalInputValue) {
      setPrincipalSliderValue(value);
    } else {
      setPrincipalSliderValue(principalInputValue);
    }
  };

  const handleTenureSliderChange = (event: ChangeEvent<HTMLInputElement>) => {
    const value = parseInt(event.target.value);
    setTenureSliderValue(value);
    setTenureInputValue(value);
  };

  const handleTenureInputChange = (event: ChangeEvent<HTMLInputElement>) => {
    const value = parseInt(event.target.value);
    setTenureInputValue(value);
    if (!tenureInputValue) {
      setTenureSliderValue(value);
    } else {
      setTenureSliderValue(tenureInputValue);
    }
  };

  const handleInterestSliderChange = (event: ChangeEvent<HTMLInputElement>) => {
    const value = parseInt(event.target.value);
    setInterestSliderValue(value);
    setInterestInputValue(value);
  };

  const handleInterestInputChange = (event: ChangeEvent<HTMLInputElement>) => {
    const value = parseInt(event.target.value);
    setInterestInputValue(value);
    if (!interestInputValue) {
      setInterestSliderValue(value);
    } else {
      setInterestSliderValue(interestInputValue);
    }
  };

  const handleSubmit = (event: FormEvent) => {
    event.preventDefault();
    const monthlyInterest = interestInputValue / 12 / 100;
    const months = tenureInputValue * 12;
    const numerator =
      principalInputValue *
      monthlyInterest *
      Math.pow(1 + monthlyInterest, months);
    const denominator = Math.pow(1 + monthlyInterest, months) - 1;
    setEMI(numerator / denominator);
    setClicked(true);
  };

  return (
    <form>
      <div className="Card">
        <div className="card-contents">
          <div className="Principal">
            <SliderField
              label="Amount you need"
              value={principalInputValue}
              onChange={handlePrincipalInputChange}
              min={50000}
              max={4000000}
            />
          </div>
          <div className="Tenure">
            <SliderField
              label="for"
              value={tenureInputValue}
              onChange={handleTenureInputChange}
              min={1}
              max={5}
            />
            <div
              style={{
                width: "150%",
                display: "flex",
                justifyContent: "space-around",
              }}
            >
              <p>1 Year</p>
              <p>5 Years</p>
            </div>
          </div>
          <div className="Interest">
            <SliderField
              label="Interest rate"
              value={interestInputValue}
              onChange={handleInterestInputChange}
              min={10.5}
              max={22}
            />
            <div
              style={{
                width: "130%",
                display: "flex",
                justifyContent: "space-around",
              }}
            >
              <p>10.5%</p>
              <p>21%</p>
            </div>
          </div>
        </div>
        <button onClick={handleSubmit}>Calculate</button>
        <EMICalculation emi={emi} clicked={clicked} />
      </div>
    </form>
  );
};

export default EMIInputForm;
